byName.map:
function(doc) {
    doc.nombre.split(' ').forEach(function(element){
        emit(element.toLowerCase(), doc.nombre);
    });
    emit(doc.nombre.toLowerCase(), doc.nombre);
}

byLevel.map:
function(doc) {
    doc.nivel.forEach(function(element) {
        emit(element.toLowerCase(), doc.nombre);
        emit(element, doc.nombre);
    });
}
